workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.common.*;

class DefaultExplorerListViewController extends ExplorerListViewController
{
    @Controller
    def controller;

    private String name;        
    private String serviceName;

    String getName() {
        if (name == null) name = controller?.workunit?.workunit?.properties?.name;
        if (name == null) name = super.getName(); 

        return name;
    } 

    String getServiceName() {
        if (serviceName == null) {
            serviceName = controller?.workunit?.workunit?.properties?.serviceName;
        } 
        return serviceName;
    } 
}
