workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.common.*;

class DefaultExplorerListViewController extends ExplorerViewerController
{
    @Controller
    def controller;

    private String scheme;        
    private String serviceName;

    String getScheme() {
        if (scheme == null) scheme = controller?.workunit?.workunit?.properties?.scheme;
        if (scheme == null) scheme = super.getScheme(); 

        return scheme;
    } 

    String getServiceName() {
        if (serviceName == null) {
            serviceName = controller?.workunit?.workunit?.properties?.serviceName;
        } 
        return serviceName;
    } 
}
