workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.common.*;

class ServiceLookupController extends LookupController
{
    @Controller
    def controller;

    private String serviceName;
    private String entityName;
    private String tag;

    String getServiceName() {
        if (serviceName == null) {
            serviceName = controller?.workunit?.workunit?.properties?.serviceName;

            if (serviceName == null) 
                throw new Exception('Please specify a serviceName attribute in your workunit'); 
        } 
        return serviceName;
    } 

    String getEntityName() {
        if (entityName == null) {
            entityName = controller?.workunit?.workunit?.properties?.entityName;
        } 
        return entityName;
    }         

    String getTag() {
        if (tag == null) {
            tag = controller?.workunit?.workunit?.properties?.tag;
        } 
        return tag; 
    } 
}
