workunit.code:

import com.rameses.osiris2.common.*;
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import java.rmi.server.*;

class AccountDetailController extends CRUDController {

    String serviceName = "AccountService";
    String entityName = "account";
    String title = "Account";
    String prefixId = "ACCT";
    def node;

    Map createEntity() {
        return [parentid:node.objid,parentcode:node.code, type:'detail'];
    }

    def subAccountModel = [
        fetchList: { o->
            return service.getSubAccounts( entity );
        },
        onAddItem: { o->
            o.objid = "SUBACCT" + new UID();
            o.state = 'APPROVED';
            o.type = "subaccount";
            o.acctgroup = entity.acctgroup;
            o.parentid = entity.objid;
            service.create(o);
        },
        onRemoveItem: { o->
            if( MsgBox.confirm("Remove this item. Continue?")) {
                service.removeEntity( o ); 
                subAccountModel.reload();
            }
        }
    ] as EditorListModel;


}
