workunit.code:

import com.rameses.osiris2.common.*;
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.util.*;

class CashReceiptInitialController  {

    @Binding
    def binding;

    @Service("CashReceiptService")
    def cashReceiptSvc;

    @Service("CollectionTypeService")
    def collectionTypeSvc;

    String title = "Cash Receipts";

    def formno;
    def txnmode;
    def formTypes;
    def collectiontype;
    def modeOpener;
    def modeOpeners;
    def collectionTypes;


    def mode;

    def getExtActions() {
        return InvokerUtil.lookupActions( "cashreceipt-initial:formActions" );
    }

    void init() {
        collectionTypes = collectionTypeSvc.getCollectionTypes();    
        formTypes = collectionTypes*.formno.unique().sort();
        modeOpeners = InvokerUtil.lookupOpeners( "cashreceipt:mode", [entity:[:]]);
        if(modeOpeners.size()>0) modeOpener = modeOpeners.first();
    }

    def getCollectionTypes()  {
        return collectionTypes.findAll{it.formno == formno };
    }

    def runProcess() {
        try {
            def entity = [:];
            txnmode = modeOpener.caption;
            entity.txnmode = txnmode;
            entity.formno = formno;
            entity.collectiontype = collectiontype;

            //call init first
            entity = cashReceiptSvc.init( entity );
            return InvokerUtil.lookupOpener("cashreceipt:"+collectiontype.handler,[entity: entity]);
        }
        catch(Warning w) {
            String m = "cashreceipt:"+w.message;
            return InvokerUtil.lookupOpener( m, [formno: formno, txnmode:txnmode ] );
        }
        catch(e) {
            throw e;
        }
    }
}
