workunit.code:

import com.rameses.osiris2.common.*;
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.util.*;

class CashReceiptInitialController  {

    @Binding
    def binding;

    @Service("CashReceiptService")
    def cashReceiptSvc;

    @Service("CollectionTypeService")
    def collectionTypeSvc;

    String title = "Cash Receipts";

    def formTypes;
    def collectiontype;
    def modeOpener;
    def modeOpeners;
    def collectionTypes;

    def entity = [:];

    def getExtActions() {
        return InvokerUtil.lookupActions( "cashreceipt-initial:formActions", [entity:entity] );
    }

    void init() {
        collectionTypes = collectionTypeSvc.getCollectionTypes();    
        formTypes = collectionTypes*.formno.unique().sort();
        modeOpeners = InvokerUtil.lookupOpeners( "cashreceipt:mode", [entity:entity]);
        if(modeOpeners.size()>0) {
            modeOpener = modeOpeners.first();
            entity.txnmode = modeOpener.caption;
        }    
    }

    def getCollectionTypes()  {
        return collectionTypes.findAll{it.formno == entity.formno };
    }

    @PropertyChangeListener
    def listener = [
        "modeOpener" : { o->
            entity.txnmode = o.caption;
        }
    ]

    def runProcess() {
        try {
            //call init first
            def newRecord = cashReceiptSvc.init( entity );
            return InvokerUtil.lookupOpener("cashreceipt:"+entity.collectiontype.handler,[entity: newRecord]);
        }
        catch(Warning w) {
            String m = "cashreceipt:"+w.message;
            return InvokerUtil.lookupOpener( m, [formno: entity.formno, txnmode: entity.txnmode ] );
        }
        catch(e) {
            throw e;
        }
    }
}
