workunit.code:

import com.rameses.osiris2.common.*;
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import java.rmi.server.*;
import com.rameses.osiris2.client.*;
import com.rameses.util.*;

class LiquidationController {

    @Binding
    def binding;

    @Service("LiquidationService")
    def service;

    String title = "Liquidation";
    def entity;
    def cashBreakdown;
    def mode = 'initial';
    def listModel;
    def selectedItem;
    def selectedCheck;

    def total = 0.0;
    def breakdown = 0.0;

    def checks = []


    def getExtActions() {
        return InvokerUtil.lookupActions( "liquidation:formActions", [entity:entity] );
    }

    def popupReports(def inv) {
        def popupMenu = new PopupMenuOpener();
        def list = InvokerUtil.lookupOpeners( inv.properties.category, [entity:entity] );
        list.each{
            popupMenu.add( it );
        }
        return popupMenu;
    }

    def init() {
        mode = "initial";
        entity = service.init();
        return "initial";
    }

    def back() {
        mode = "initial";
        return "initial";
    }

    void setSelectedItem( def selectedItem) {
       this.selectedItem = selectedItem 
       cashBreakdown = InvokerUtil.lookupOpener("cash:breakdown", [
            entries: selectedItem.cashbreakdown, onupdate: { o->
                selectedItem.breakdown = o;
                binding.refresh("selectedItem.breakdown");
            }] );
        binding.refresh("selectedItem.*")
        checkModel.reload()
    }


    def start() {
        mode = "create";
        checks = entity.checks.clone()  
        selectedItem = entity.fundsummary[0] 
        cashBreakdown = InvokerUtil.lookupOpener("cash:breakdown", [
            entries: selectedItem.cashbreakdown, onupdate: { o->
                selectedItem.breakdown = o;
                binding.refresh("selectedItem.breakdown");
            }] );
        return "default";
    }

    def open(){
        mode = "read";
        entity = service.open(entity);
        selectedItem = entity.fundsummary[0] 
        cashBreakdown = InvokerUtil.lookupOpener("cash:breakdown", [entries: entity.cashbreakdown, allowEdit:false] );    
        total = entity.totalcash + entity.totalnoncash;
        breakdown = entity.cashbreakdown.sum{it.amount};
    }

    void submit() {
        for( o in entity.fundsummary) {
            if( o.breakdown != o.totalcash) 
                throw new Exception("Cash breakdown must equal total cash");
        }


        if( MsgBox.confirm("You are about to submit this liquidation. Please ensure the entries are correct")) {
            entity.cashbreakdown = []
            entity = service.post( entity );
            mode = "read";
            MsgBox.alert("Posting successful");
        }
    }

    def addCheck() {
        return InvokerUtil.lookupOpener( "undepositedcheck:lookup", 
            [
                undepositedChecks: checks,
                onselect: { arr->
                    if (arr) {
                        arr.each {
                            selectedItem.checks << it;
                            checks.remove(it);
                        }
                        selectedItem.totalnoncash = selectedItem.checks.amount.sum();
                        selectedItem.totalcash =  selectedItem.amount - selectedItem.totalnoncash
                        checkModel.reload();
                        binding.refresh("selectedItem.totalnoncash|selectedItem.totalcash");
                    }
                }
            ]
       );
    }

    void removeCheck() {
        if(!selectedCheck) return;
        selectedItem.checks.remove( selectedCheck );
        selectedCheck.selected = false
        checks << selectedCheck;

        if( selectedItem.checks ) {
            selectedItem.totalnoncash = selectedItem.checks.sum{it.amount};
        }
        else {
            selectedItem.totalnoncash = 0;
        }
        checkModel.reload();
        binding.refresh("totalnoncash|total");
    }


    def remittancesModel = [
        fetchList: { o->return entity.remittances; }
    ] as BasicListModel;

    def fundSummaryModel = [
        fetchList: { o->return entity.fundsummary.each{
                        it.totalnoncash = 0.0
                        it.totalcash = it.amount 
                        it.cashbreakdown =[]
                        it.checks = []
                        it.breakdown = 0.0
                }; }
    ] as EditorListModel;

    def checkModel = [
        fetchList: {
            return selectedItem.checks;
        }
    ] as BasicListModel;

}
