import com.rameses.osiris2.common.*;
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import java.rmi.server.*;
import com.rameses.osiris2.client.*;
import com.rameses.util.*;
import com.rameses.osiris2.reports.*;

class UndepositedCheckLookupController extends LookupController {

    def undepositedChecks;

    boolean multiSelect = true;

    public List getColumnList() {
        return [
            [name:'checkno', caption:'Check No', width:80],
            [name:'particulars', caption:'Particulars'],
            [name:'amount', caption:'Amount', type:'decimal', width:80],
        ]
    }

    public List fetchList( Map m ) {
        return undepositedChecks;
    }

}
