workunit.code:


import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*

class  AFRequestAddItem  {

    @Service("AFService")
    def afSvc;

    def entity;
    def items = [];
    def saveHandler;

    void init() {
        def afTypes = afSvc.getList([:]);
        items = afTypes.collect{ [af:it.af, unit: it.unit, qty: 0 ] };
        //if entity not null, copy all attributes 
        if(entity?.items) {
            def existItems = entity.items;
            items.each { o->
                def z = existItems.find{ it.af == o.af };
                if(z) o.putAll(z);
            }
        }
    }

    def listModel = [
        fetchList: { o->
            return items;
        }
    ] as EditorListModel;

    def doOk() {
        entity.items = [];
        def subItems = items.findAll{ it.qty > 0 };
        subItems.each {
            entity.items << it;
        }
        saveHandler();
        return "_close";
    }

    def doCancel() {
        return "_close";
    }
}
