workunit.code:

import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*
import com.rameses.enterprise.treasury.cashreceipt.*

class  SelectAFController  {

    @Service("AFControlService")
    def svc;

    def handler;
    def selectedItem;
    def entity;
    def list;

    void init() {
        if(!entity.formno)
            throw new Exception("Please select an accountable form first");
    }

    def listModel = [
        fetchList: { o->
            list = svc.getOpenControlList( [af:entity.formno, txnmode:entity.txnmode] );
            return list;
        }
    ] as BasicListModel;

    def doOk() {
        //find also the current actives.
        def old = list.find{ it.active == 1 };
        svc.activateControl([objid:selectedItem.objid, oldid:old?.objid]);
        MsgBox.alert('Control activated');
        return "_close";
    }

    def doCancel() {
        return "_close";
    }
}
