workunit.code:

import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*
import com.rameses.enterprise.treasury.cashreceipt.*

class  SelectAFController  {

    @Service("AFControlService")
    def svc;

    @Caller
    def caller

    def formno;
    def txnmode;
    def handler;
    def selectedItem;

    void init() {
        formno = caller.formno;
        txnmode = caller.txnmode;
        if(!formno)
            throw new Exception("Please select an accountable form first");
    }

    def listModel = [
        getColumnList: { o->
            return [
                [name:'startseries', caption:'Start Series'],
                [name:'endseries', caption:'End Series'],
                [name:'stub', caption:'Stub']
            ]
        },
        fetchList: { o->
            return svc.getOpenControlList( [af:formno, txnmode:txnmode] );
        }
    ] as BasicListModel;

    def doOk() {
        svc.activateControl(selectedItem);
        MsgBox.alert('Control activated');
        return "_close";
    }

    def doCancel() {
        return "_close";
    }
}
