workunit.code:

import com.rameses.osiris2.common.*;
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import java.rmi.server.*;
import com.rameses.osiris2.client.*;
import com.rameses.util.*;
import com.rameses.osiris2.reports.*;

class RemittanceCollectionTypeReportController {

    @Binding
    def binding;

    @Service("RemittanceReportService")
    def svc

    @Service("ReportParameterService")
    def paramsvc

    @Service("CollectionTypeService")
    def collectiontypesvc

    def collectionTypeList
    def selectedcollectiontype

    def entity;
    def reportdata;
    def reportPath = "com/rameses/gov/treasury/remittance/report/"
    def mode = 'init'

    String title = "Remittance Report By Collection Type";

    void init() {
        mode = 'init'
        collectionTypeList = collectiontypesvc.getCollectionTypes()
    }

    def viewReport() {
        reportdata = svc.generateReportByCollectionType( entity, selectedcollectiontype)
        report.viewReport();
        mode = 'view'
        return 'preview'
    }

    def back() {
       if(mode == 'view') {
            mode = 'init'
            return 'default'
       }

       return '_close';
    }

    def report = [
        getReportName : { return reportPath + 'reportbycollectiontype.jasper' },
        getReportData : { return reportdata }, 
        getParameters : { 
            def params = paramsvc.getStandardParameter() 
            params.SUBTITLE = 'SUMMARY OF COLLECTION'
            if (selectedcollectiontype)
                params.SUBTITLE = params.SUBTITLE + ' (' + selectedcollectiontype.name + ')';
            return params;

        },
        getSubReports : {
            return [ 
                new SubReport("ReportBItem", reportPath + "reportbycollectiontypeitem.jasper"),
            ] as SubReport[];    
         }
    ] as ReportModel

}
