workunit.code:

import com.rameses.osiris2.common.*;
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import java.rmi.server.*;
import com.rameses.osiris2.client.*;
import com.rameses.util.*;
import com.rameses.osiris2.reports.*;

class RemittanceRevenueItemReportController {

    @Binding
    def binding;

    @Service("RemittanceReportService")
    def svc

    @Service("ReportParameterService")
    def paramsvc

    @Service("FundService")
    def fundsvc

    def selectedfund
    def fundList

    def entity;
    def reportdata;
    def reportPath = "com/rameses/gov/treasury/remittance/report/"
    def mode = 'init'

    String title = "Remittance Report By Revenue Item";

    void init() {
        mode = 'init'
        fundList = fundsvc.getList( [:] )
    }

    def viewReport() {
        reportdata = svc.generateReportByRevenueItem( entity, selectedfund)
        report.viewReport();
        mode = 'view'
        return 'preview'
    }

    def back() {
       if(mode == 'view') {
            mode = 'init'
            return 'default'
       }

       return '_close';
    }

    def report = [
        getReportName : { return reportPath + 'reportbyrevenueitem.jasper' },
        getReportData : { return reportdata }, 
        getParameters : { 
            def params = paramsvc.getStandardParameter() 
            params.SUBTITLE = 'SUMMARY OF COLLECTION'
            if (selectedfund) {
                params.SUBTITLE = params.SUBTITLE + ' BY ' + selectedfund.title + ' FUND ' ;
            }
            return params;
        },
        getSubReports : {
            return [ 
                new SubReport("ReportSummary", reportPath + "reportbyrevenueitemsummary.jasper"),
            ] as SubReport[];    
         }
    ] as ReportModel

}
