import com.rameses.osiris2.common.*;
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;

class SREAccountGroupController extends CRUDController {

    String serviceName = "SREAccountService";
    String entityName = "sreaccount";
    String title = "SRE Account";
    String prefixId = "SRE";
    def node;

    Map createEntity() {
        return [parentid:node.objid,parentcode:node.code, type:'group'];
    }

}
