import com.rameses.rcp.annotations.*;
import com.rameses.rcp.common.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.common.*;

public class RuleSetController extends CRUDController {

    String prefixId = 'RSET';
    String createFocusComponent = 'entity.name'; 
    String editFocusComponent = 'entity.title'; 
    boolean allowApprove = false; 

    @Service('RuleSetService')  
    def service;

    def tabHandler = [
        getOpeners: { 
            return tabHandler.lookupOpeners('ruleset:plugins', [:]); 
        },

        getOpenerParams: {
            return [entity: entity];
        } 
    ] as TabbedPaneModel 

    void entityChanged() {
        tabHandler.reload(); 
    } 
}
