workunit.code:


import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.common.*
import com.rameses.osiris2.reports.*;

class RuleConstraintMultiLookup {

    @Binding
    def binding;

    def lookupHandler;
    def lookupKey;
    def lookupValue;
    def items;
    def handler;

    def getAddItem() {
        return InvokerUtil.lookupOpener( lookupHandler, [
            multiSelect: true,
            multiSelectHandler: { o->
                return (items.find{ it.key == o[lookupKey] }!=null);
            },
            onselect: { o->
                o.each {
                    items << [ key: it[lookupKey], value: it[lookupValue] ] ;
                }
                listModel.reload();
            }
        ]);
    }

    def listModel = [
        fetchList: { o->
            return items;
        },
        onRemoveItem: { o->
            items.remove(o);
        }
    ] as EditorListModel;

    def doOk() {
        handler( items );
        return "_close";
    }

    def doCancel() {
        return "_close";
    }
}
