import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*
import com.rameses.common.*;

class RuleExpressionFormulaController extends BasicListModel
{        
    @Binding
    def binding;

    def list;
    def onselect;
    def selectedEntity;

    def listHandler = this;

    public Column[] getColumns() {
        return [
            new Column( name:'name', caption:'Title' )
        ] as Column[];
    }

    public List fetchList(Map map ) {
        return list;
    }

    def doSelect() {
        onselect(selectedValue);
        return "_close";
    }

    def doCancel() {
        return "_close";
    }
}
